CREATE TABLE employers (employerId INTEGER AUTO_INCREMENT NOT NULL, employerName VARCHAR(255), PRIMARY KEY (employerId))
CREATE TABLE jobs (jobId INTEGER AUTO_INCREMENT NOT NULL, JOBNAME VARCHAR(255), PRIMARY KEY (jobId))
CREATE TABLE users (userId INTEGER AUTO_INCREMENT NOT NULL, BIRTHDATE DATE, CONTRACTHOURS INTEGER, userEmail VARCHAR(255), ENABLED TINYINT(1) default 0, FIRSTNAME VARCHAR(255), FOURWEEKPAYOFF TINYINT(1) default 0, INSERTION VARCHAR(255), LASTNAME VARCHAR(255), userPassword VARCHAR(255), PASSWORDEXPIRE DATETIME, role INTEGER, SEX VARCHAR(255), ZEROHOURS TINYINT(1) default 0, employerId INTEGER, workScheduleId INTEGER, PRIMARY KEY (userId))
CREATE TABLE user_keys (ID INTEGER AUTO_INCREMENT NOT NULL, EMAIL VARCHAR(255), key_type VARCHAR(255), SECRET BIGINT, PRIMARY KEY (ID))
CREATE TABLE workschedules (workScheduleId INTEGER AUTO_INCREMENT NOT NULL, CREATIONTIME DATETIME, FRIDAY VARCHAR(255), MONDAY VARCHAR(255), SATURDAY VARCHAR(255), SUNDAY VARCHAR(255), THURSDAY VARCHAR(255), TUESDAY VARCHAR(255), WEDNESDAY VARCHAR(255), PRIMARY KEY (workScheduleId))
CREATE TABLE users_to_jobs (jobId INTEGER NOT NULL, userId INTEGER NOT NULL, PRIMARY KEY (jobId, userId))
ALTER TABLE users ADD CONSTRAINT FK_users_workScheduleId FOREIGN KEY (workScheduleId) REFERENCES workschedules (workScheduleId)
ALTER TABLE users ADD CONSTRAINT FK_users_employerId FOREIGN KEY (employerId) REFERENCES employers (employerId)
ALTER TABLE users_to_jobs ADD CONSTRAINT FK_users_to_jobs_userId FOREIGN KEY (userId) REFERENCES users (userId)
ALTER TABLE users_to_jobs ADD CONSTRAINT FK_users_to_jobs_jobId FOREIGN KEY (jobId) REFERENCES jobs (jobId)
